import logo from './logo.svg';
import './App.css';
import React, { useState } from "react";

function App() {
  const [urls, setUrls] = useState("https://www.google.com, https://www.github.com");
  const [screenshot, setScreenshot] = useState(null);

  const takeScreenshot = async () => {
    try {
      const res = await fetch(`http://localhost:5000/screenshot?url=https://google.com&t=${Date.now()}`);
      console.log(res);

      if (!res.ok) {
        throw new Error("Failed to take screenshot");
      }

      const blob = await res.blob();
      setScreenshot(URL.createObjectURL(blob));
    } catch (err) {
      console.error(err);
      alert("Failed to fetch screenshot");
    }
  };

  return (
    <div className="App">
      <h1> Puppeteer example </h1>
      <textarea
      rows={4}
      columns={10}
      value={urls}
      onChange={ (e) => setUrls(e.target.value) }
      placeholder="Enter comma separated values"
      />

      <button onClick={takeScreenshot}>Take Screenshot</button>
      <h2> The screenshot once taken will be added below : </h2>
      {screenshot && <img src={screenshot} alt="Screenshot" />}

    </div>
  );
}

export default App;
